$columns = @(
    'Name',
    @{
        Name = 'EventGroup'
        Expression = {
            $def = $_
            $def.EventTypeGroupValues.Keys | Where-Object {
                $def.eventtypegroupvalues[$_] -eq $def.EventTypeGroup
            }
        }
    },
    @{
        Name = 'Event'
        Expression = {
            $def = $_
            $def.EventTypeValues.Keys | Where-Object {
                $def.eventtypevalues[$_] -eq $def.EventType
            }
        }
    }
)

Get-VmsAlarmDefinition | Select-Object $columns

Get-VmsClientProfile | Foreach-Object {
    $general = $_ | Get-VmsClientProfileAttributes -Namespace General
    [pscustomobject]@{
        Name      = $_.Name
        StartView = $general.ApplicationStartView.Value
    }
}
