Name                         StartView
----                         ---------
Operator Profile             Last
Default Smart Client Profile Last
#>

Get-VmsFailoverGroup | Foreach-Object {
    $group = $_
    Get-VmsFailoverRecorder -FailoverGroup $group | Foreach-Object {
        [pscustomobject]@{
            FailoverGroup    = $group.Name
            FailoverRecorder = $_.Name
        }
    }
}

Get-VmsHardware | Foreach-Object {
    [pscustomobject]@{
        Address  = $_.Address
        UserName = $_.UserName
        Password = $_ | Get-VmsHardwarePassword
    }
}

function Get-VmsRoleClaimReport {
    $providers = Get-VmsLoginProvider
    foreach ($role in Get-VmsRole) {
        foreach ($claim in Get-VmsRoleClaim) {
            [pscustomobject]@{
                Role     = $role.Name
                Provider = ($providers | Where-Object Id -eq $claim.ClaimProvider).Name
                Claim    = $claim.ClaimName
                Value    = $claim.ClaimValue
            }
        }
    }
}
Get-VmsRoleClaimReport | Export-Csv -Path ~\Desktop\Vms-Role-Claim-Report.csv -NoTypeInformation

