command:example.dev:code:
$macProperty = @{
    Name       = 'MACAddress'
    Expression = { ($_ | Get-HardwareSetting).MACAddress }
}
Get-VmsHardware | Select-Object Name, Address, $macProperty

$splat = @{
    StartTime = (Get-Date).AddDays(-90)
    EndTime   = Get-Date
    MatchList = 'Unlisted license plate'
}
Get-VmsLprEvent @splat | Select-Object Timestamp, ObjectValue, SourceName | Export-Csv .\lprevents.csv

$splat = @{
    StartTime = (Get-Date).AddDays(-90)
    EndTime   = Get-Date
    MatchList = 'Unlisted license plate'
}
$properties = @(
    'Timestamp',
    @{n='RegistrationNumber'; e={$_.ObjectValue}},
    @{n='Camera';             e={$_.SourceName}},
    @{n='PlateStyle';         e={($_.ObjectData | ConvertFrom-Json).PlateStyleId}}
)
Get-VmsLprEvent @splat | Select-Object $properties | Export-Csv lprevents.csv

$destination = 'C:\temp\'
$camera = Select-Camera -SingleSelect
$camera | Get-VmsMetadataLiveRecord -Until (Get-Date).AddHours(1) | Where-Object {
    'Vehical' -in $_.GetMetadataStream().VideoAnalyticsItems.Frames.Objects.Appearance.Class.ClassCandidates.Type
} | ForEach-Object {
    $camera | Get-Snapshot -Timestamp $_.GetMetadataStream().VideoAnalyticsItems.Frames[0].UtcTime -Quality 100 -Save -Path $destination
} | Select-Object DateTime, Width, Height, HardwareDecodingStatus

$destination = 'C:\temp\'
$camera = Select-Camera -SingleSelect
$camera | Get-VmsMetadataRecord -Timestamp (Get-Date).AddHours(-1) -Until (Get-Date) | Where-Object {
    'Face' -in $_.GetMetadataStream().VideoAnalyticsItems.Frames.Objects.Appearance.Class.ClassCandidates.Type
} | ForEach-Object {
    $camera | Get-Snapshot -Timestamp $_.GetMetadataStream().VideoAnalyticsItems.Frames[0].UtcTime -Quality 100 -Save -Path $destination
} | Select-Object DateTime, Width, Height, HardwareDecodingStatus

# Export all Parking* match lists to CSV files Get-VmsMatchList -Name Parking* | ForEach-Object { $_ | Get-VmsLprMatchListEntry | Export-Csv ".\MatchList - $($_.Name).csv" }
# Remove all Parking* match lists Get-VmsMatchList -Name Parking* | Remove-VmsMatchList
#Re-import all Parking* match lists with all their original registration numbers and custom fields Import-VmsLprMatchList -Path .\MatchList*.csv

$lock = Get-EvidenceLock -DeviceIds 3D9DD26B-9A48-43D2-BFEA-3FEB6E8CD2EE
$lock.DeviceIds = $lock.DeviceIds | Where-Object { $_ -ne '3D9DD26B-9A48-43D2-BFEA-3FEB6E8CD2EE' }
if ($lock.DeviceIds.Count -eq 0) {
    $lock | Remove-EvidenceLock -Force
} else {
    $lock | Update-EvidenceLock    
}
